import React from "react";

const format_time = time => (time < 10 ? `0${time}:00u` : `${time}:00u`);

export default ({ time, patient, dentist, removeAppointment, editAppointment, id }) => {
    if (dentist.isIll){
        return (
        <div className="appointment ill">
            <span className="time">{format_time(time)}</span>
            <span className="patient">{`${patient.firstname} ${patient.surname}`}</span>
            <span role="img" aria-label="icon" className="icon" onClick={() => {removeAppointment(id)}}>ðŸš«</span>
            <span role="img" aria-label="icon" className="icon" onClick={() => {editAppointment(id)}}><span role="img"aria-label="icon">ðŸ•‘</span></span>
        </div>
        )
    }else {
        return(
        <div className="appointment">
            <span className="time">{format_time(time)}</span>
            <span className="patient">{`${patient.firstname} ${patient.surname}`}</span>
            <span role="img" aria-label="icon" className="icon" onClick={() => {removeAppointment(id)}}>ðŸš«</span>
            <span role="img" aria-label="icon" className="icon" onClick={() => {editAppointment(id)}}><span role="img"aria-label="icon">ðŸ•‘</span></span>
        </div>
        )
    }
}
